import { Component, OnInit } from '@angular/core';
import { CursoService } from './curso.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  cursos: any;

  curso = {
    idcurso: 0,
    nom_curso: '',
    estado: 'Activo'
  };

  constructor(private cursosServicio: CursoService) {}

  ngOnInit() {
    this.recuperarTodos();
  }

  recuperarTodos() {
    this.cursosServicio.recuperarTodos().subscribe((result: any) => (this.cursos = result));
  }

  alta() {
    this.cursosServicio.alta(this.curso).subscribe((datos: any) => {
      if (datos['resultado'] === 'OK') {
        alert(datos['mensaje']);
        this.recuperarTodos();
      }
    });
  }

  baja(idcurso: number) {
    this.cursosServicio.baja(idcurso).subscribe((datos: any) => {
      if (datos['resultado'] === 'OK') {
        alert(datos['mensaje']);
        this.recuperarTodos();
      }
    });
  }

  modificacion() {
    // Solo permitir modificar el estado del curso
    const estadoModificadoCurso = {
      idcurso: this.curso.idcurso,
      estado: this.curso.estado
    };

    this.cursosServicio.modificacion(estadoModificadoCurso).subscribe((datos: any) => {
      if (datos['resultado'] === 'OK') {
        alert(datos['mensaje']);
        this.recuperarTodos();
      }
    });
  }

  seleccionar(idcurso: number) {
    this.cursosServicio.seleccionar(idcurso).subscribe((result: any) => (this.curso = result[0]));
  }

  hayRegistros() {
    return this.cursos && this.cursos.length > 0;
  }
}
<div>
  <h1 class="display-4">CRUD CURSO</h1>
  <table class="table" *ngIf="hayRegistros(); else sincursos">
    <thead class="table-dark">
      <tr>
        <th scope="col">IdCurso</th>
        <th scope="col">Nom_Curso</th>
        <th scope="col">Estado</th>
        <th scope="col">Borrar</th>
        <th scope="col">Seleccionar</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let curso of cursos">
        <td>{{curso.idcurso}}</td>
        <td>{{curso.nom_curso}}</td>
        <td>{{curso.estado}}</td>
        <td><button class="btn btn-danger" (click)="baja(curso.idcurso)">Borrar</button></td>
        <td><button class="btn btn-primary" (click)="seleccionar(curso)">Seleccionar</button></td>
      </tr>
    </tbody>
  </table>
  <ng-template #sincursos><p class="lead">No hay cursos.</p></ng-template>

  <div *ngIf="cursoSeleccionado">
    <p class="lead">
      IdCurso: {{cursoSeleccionado.idcurso}}
    </p>
    <p class="lead">
      Nom_Curso: {{cursoSeleccionado.nom_curso}}
    </p>
    <p class="lead">
      Estado:
      <select class="form-control" [(ngModel)]="cursoSeleccionado.estado">
        <option value="Activo">Activo</option>
        <option value="Inactivo">Inactivo</option>
      </select>
    </p>
    <p>
      <button class="btn btn-warning" (click)="modificacion()">Modificar</button>
    </p>
  </div>
</div>
