import { LoginComponent } from './../components/login/login.component';
import { Component } from '@angular/core';
import { LoginService } from '../login.service';

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css','/DIGIWORM_04/src/app']
})
export class ChatComponent {
title='chat'
email: string = '';
password: string = '';

  onSubmit() {
    // Add your login logic here
    // You can use services to handle authentication
  }

}
<div class="wrapper">
  <section class="form login">
    <header>Ingresar a Chat en Línea</header>
    <form (ngSubmit)="onSubmit()" autocomplete="off">
      <div class="error-text"></div>
      <div class="field input">
        <label>Dirección de Correo Electrónico</label>
        <input type="text" name="email" placeholder="Ingresa tu Correo Registrado" required [(ngModel)]="email">
      </div>
      <div class="field input">
        <label>Contraseña</label>
        <input type="password" name="password" placeholder="Ingresa tu Contraseña" required [(ngModel)]="password">
        <i class="fas fa-eye"></i>
      </div>
      <div class="field button">
        <button type="submit">Chatear</button>
      </div>
    </form>
    <div class="link">Aún no te has registrado? <a routerLink="/">Ingresar</a></div>
  </section>
</div>
